SELECT CONCAT(Firstname, ' ',Lastname) AS 'FULL NAME', lib_region.region_name AS 'REGION NAME', lib_cycle.name AS 'Cycle' FROM PersonProfile INNER JOIN lib_region ON lib_region.region_code = PersonProfile.region_code INNER JOIN isVolunteerTable ON PersonProfile.PersonProfileId = IsVolunteerTable.PersonProfileId INNER JOIN lib_cycle ON lib_cycle.cycle_id = IsVolunteerTable.cycle_id WHERE lib_region.region_code = '070000000' OR lib_region.region_name = 'REGION VII [Central Visayas]';